package week1.day1;

public class LearnDataTypeAndVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Below are the primitive data types
		String brandName = "IPhone";
		String model = "6-S";
		
		//Below are the non-primitive data types
		char colour = 'S';
		int cost = 36000;
		byte memory = 115;
		Float screenResolution = 15.6f;
		boolean isTouchScreen = false;
		long mobileNumber =9123466666L;
		
		System.out.println("My Phone's Brand Name is: " + brandName);
System.out.println("I bought it for a cost of: " + cost);
System.out.println("My Mobile Number is: " + mobileNumber);
System.out.println("I am happy to have my favourite colour for the phone: " + colour);
	}

}
