package week1.day1;

import java.util.Iterator;

public class LearnIfAndFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// To print all the even numbers between 1 and 50
		for(int i =1; i<=50; i++) {
			if (i%2==0) {
				System.out.println(i);
			}
		}
	}

	 
}
